    /**
     * Creates a new languagefilter
     * This will filter out all unwanted languages (remove the tweets from the list)
     * @param allowedLanguages the languages or NULL, if all langs are allowed
     * @throws FilterException if anything goes wrong (which will not)
     */
    public LanguageFilter(List<String> allowedLanguages) throws FilterException{
        this.allowedLanguages = allowedLanguages;
        
        DetectorFactory.clear();
        ArrayList<String> profiles = new ArrayList<>();
        profiles.add(lang_en);
        profiles.add(lang_fr);
        profiles.add(lang_de);
        try {
            DetectorFactory.loadProfile(profiles);
        } catch (LangDetectException ex) {
            throw new FilterException(ex);
        }
        
        
    }
    
    @Override
    public applyMode getApplyMode() {
        return applyMode.BEFORE;
    }
    
    @Override
    public List<Tweet> applyFilter(List<Tweet> tweets) {
        for (Tweet t : tweets) {
            try {
                Detector detector = DetectorFactory.create();
                detector.append(t.getContent());
                String lang = detector.detect();
                
                //@TODO: check if lang is allowed
                t.setLanguage(lang);
            } catch (LangDetectException ex) {
                Logger.getLogger(LanguageFilter.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
        
        
        return tweets;
    }

    
    
